SELECT DISTINCT [3D_FAI17] AS pin
FROM [MESDataCenter].[dbo].[m_X2987_DATA_t]
WHERE LineName like concat('%', #{senseDataDto.LineID}, '%')
  and CONVERT(VARCHAR(20), Collecttime, 120) >= #{senseDataDto.startTime}
  AND CONVERT(VARCHAR(20), Collecttime, 120) <= #{senseDataDto.endTime}
